ROOT_DIR="$(dirname "$( cd "$( dirname "${BASH_SOURCE[0]}" )" >/dev/null 2>&1 && pwd )")"
OUTPUT_DIR=$ROOT_DIR/docs/archive-images/

mkdir -p $OUTPUT_DIR

# DOCS:     https://github.com/jsvine/waybackpack
# PREPARE:  pip install waybackpack tqdm
# NOTES:    needs tqdm for progress bar

for i in "${arr[@]}"
do
  waybackpack --no-clobber --follow-redirects --raw --progress "$i" -d $OUTPUT_DIR --from-date $from_year --to-date $current_year
done

